import { notification } from "antd";
import { ofType } from "redux-observable";
import { merge, of } from "rxjs";
import {
  catchError,
  concatMap,
  delay,
  map,
  withLatestFrom,
} from "rxjs/operators";
import { UserActions } from "./actions";
import { User } from "./models";
import { getUserMe, postUser } from "./service";

export interface Action<T = undefined> {
  type: string;
  payload?: T;
}

const getUserMeByCpf = (cpf: string) => {
  return getUserMe(cpf).pipe(
    map((response: User) => {
      console.log(response);
      return UserActions.getUserMeSuccess(response);
    }),
    catchError((error) => {
      console.log(error);
      return of(UserActions.getUserMeFailure(error));
    })
  );
};

export const handleGetUserMe = (action$: Promise<any>) =>
  action$.pipe(
    ofType("GET_USER_ME"),
    map((action: Action<object>) => action.payload),
    concatMap((payload: string) => getUserMeByCpf(payload))
  );

const registerUser = (user: User) => {
  return postUser(user).pipe(
    map((response: User) => {
      console.log(response);
      return UserActions.getUserMeSuccess(response);
    }),
    catchError((error) => {
      console.log(error);
      return of(UserActions.getUserMeFailure(error));
    })
  );
};

export const handleRegisterUser = (action$: Promise<any>) =>
  action$.pipe(
    ofType("REGISTER_USER"),
    map((action: Action<object>) => action.payload),
    concatMap((newUser: User) => registerUser(newUser))
  );
